import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar } from "@fortawesome/free-solid-svg-icons";
import MovieModal from "./MovieModal";
import { Modal, Button } from "react-bootstrap";

//TODO: Call Movie/Details/:id API (UseEffect)

const UnlikedMovies = ({ movie }) => {
  const [imageURL, setImageURL] = useState("https://image.tmdb.org/t/p/w500");
  const [isOpen, setIsOpen] = useState(false);

  const openModal = () => setIsOpen(true);
  const closeModal = () => setIsOpen(false);

  return (
    <div className="history-movie" onClick={openModal}>
      <Modal show={isOpen} onHide={closeModal} style={{ opacity: 1 }}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Close
          </Button>
          <Button variant="primary" onClick={closeModal}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      <div className="py-1 px-2">
        <img
          src={imageURL + movie[0].poster_path}
          className="w-20 rounded-lg shadow-lg"
        />
      </div>
      <div className="py-5 flex flex-col">
        <h1 className="font-semibold text-sm px-1">{movie[0].title}</h1>
        <span className="font-light text-gray-400 text-sm">
          {movie[0].release_date}
        </span>
        <div className="flex items-center text-sm">
          <FontAwesomeIcon
            icon={faStar}
            className={
              movie[0].vote_average / 2 >= 1 ? "text-red-500" : "text-gray-400"
            }
          />
          <FontAwesomeIcon
            icon={faStar}
            className={
              movie[0].vote_average / 2 >= 2 ? "text-red-500" : "text-gray-400"
            }
          />
          <FontAwesomeIcon
            icon={faStar}
            className={
              movie[0].vote_average / 2 >= 3 ? "text-red-500" : "text-gray-400"
            }
          />
          <FontAwesomeIcon
            icon={faStar}
            className={
              movie[0].vote_average / 2 >= 4 ? "text-red-500" : "text-gray-400"
            }
          />
          <FontAwesomeIcon
            icon={faStar}
            className={
              movie[0].vote_average / 2 >= 5 ? "text-red-500" : "text-gray-400"
            }
          />
        </div>
      </div>
    </div>
  );
};

export default UnlikedMovies;
